@import url('https://fonts.googleapis.com/css2?family=Poppins:ital,wght@0,100;0,200;0,300;0,400;0,500;0,600;0,700;0,800;0,900;1,100;1,200;1,300;1,400;1,500;1,600;1,700;1,800;1,900&display=swap');

* {
    padding: 0;
    margin: 0;
    box-sizing: border-box;
}

.course {
    position: absolute;
    left: 0;
    top: 0;
    height: 2000px;
    width: 100%;
    font-family: Poppins;
    /* background: #ecfafa; */
    background: linear-gradient(#000,90%,#fff);
}

.course .title1 {
    position: absolute;
    width: 120px;
    height: 48px;
    top: 550px;
    left: 10%;
    color: #fff;
    font-size: 48px;
    font-weight: 600;
    letter-spacing: 6px;
    z-index: 2;
}

/* banner样式 */
.course .slider {
    height: 100vh;
    top: 0px;
    position: relative;
}

.course .slider .list .item {
    position: absolute;
    inset: 0 0 0;
    overflow: hidden;
    opacity: 0;
    transition: .5s;
}

.course .slider .list .item img {
    width: 100%;
    height: 100%;
    object-fit: cover;
}

.course .slider .list .item::after {
    content: '';
    width: 100%;
    height: 100%;
    position: absolute;
    left: 0;
    bottom: 0;
    background-image: linear-gradient(
        to top,#000 40%,transparent
    );
}

.course .slider .list .item .content {
    position: absolute;
    left: 10%;
    top: 20%;
    width: 500px;
    max-width: 80%;
    z-index: 1;
    color: #fff;
}

.course .slider .list .item .content p:nth-child(1) {
    text-transform: uppercase;
    letter-spacing: 10px;
}

.course .slider .list .item .content h2 {
    font-size: 100px;
    margin: 0;
}

.course .slider .list .item.active {
    opacity: 1;
    z-index: 1;
}

@keyframes showContent {
    to{
        transform: translateY(0);
        filter: blur(0);
        opacity: 1;
    }
}

.course .slider .list .item.active p:nth-child(1),
.course .slider .list .item.active h2,
.course .slider .list .item.active p:nth-child(3){
    transform: translateY(30px);
    filter: blur(20px);
    opacity: 0;
    animation: showContent .5s .7s ease-in-out 1 forwards;
}
.course .slider .list .item.active h2{
    animation-delay: 1s;
}
.course .slider .list .item.active p:nth-child(3){
    animation-duration: 1.3s;
}

/* banner 按钮 */
.course .arrows{
    position: absolute;
    top: 38%;
    left: 45%;
    z-index: 1;
}
.course .arrows button{
    background-color: #eee5;
    border: none;
    font-family: monospace;
    width: 40px;
    height: 40px;
    border-radius: 5px;
    font-size: x-large;
    color: #eee;
    transition: .5s;
}
.course .arrows button:hover{
    background-color: #eee;
    color: black;
}

/* banner 缩略图 */
.course .thumbnail{
    position: absolute;
    top: 500px;
    z-index: 1;
    display: flex;
    gap: 10px;
    width: 100%;
    height: 250px;
    padding: 0 50px;
    color: #fff;
    box-sizing: border-box;
    overflow: auto;
    justify-content: center;
}
.course .thumbnail::-webkit-scrollbar{
    width: 0;
}
.course .thumbnail .item{
    width: 150px;
    height: 220px;
    filter: brightness(.5);
    transition: .5s;
    flex-shrink: 0;
}
.course .thumbnail .item img{
    width: 100%;
    height: 100%;
    object-fit: cover;
    border-radius: 10px;
}
.course .thumbnail .item.active{
    filter: brightness(1.5);
}
.course .thumbnail .item .content{
    position: absolute;
    inset: auto 10px 10px 10px;
}
@media screen and (max-width: 678px) {
    .thumbnail{
        justify-content: start;
    }
    .slider .list .item .content h2{
        font-size: 60px;
    }
    .arrows{
        top: 10%;
    }
}

/* 课程分类导航栏 */
.course .title2 {
    position: absolute;
    width: 200px;
    height: 30px;
    top: 900px;
    left: 5%;
    color: #fff;
    font-size: 30px;
    font-weight: 600;
    letter-spacing: 2px;
}

.course .nav {
    position: absolute;
    top: 980px;
    left: 5%;
    border: none;
    border-radius: 8px;
    display: flex;
    list-style: none;
    background: #222;
    padding: 8px;
}
.course .nav a {
    position: relative;
    font: 500 16px 'Poppins'; 
    text-decoration: none;
    color: #fff;
    padding: 6px 23px;
    height: 100%;
    border-radius: 8px;
    transition: .5s;
}

.course .nav a:hover {
    background: #5e5e5e;
}

/* 课程卡片 */
.course .container {
    position: absolute;
    top: 1050px;
    width: 100%;
    left: 51%;
    display: flex;
    justify-content: left;
    align-items: flex-start;
    flex-wrap: wrap;
    padding: 0px 50px;
}

.course .container .card {
    position: relative;
    width: 300px;
    height: 250px;
    display: flex;
    justify-content: center;
    align-items: flex-start;
    background: #f3f3f3;
    border-radius: 20px;
    box-shadow: 0 35px 80px rgba(0, 0, 0, 0.15);
    transition: .5s;
    margin: 50px 15px;
}

.course .container .card:hover {
    height: 350px;
    margin: 0px 15px;
}

.course .container .card .imgBx {
    position: absolute;
    top: 20px;
    width: 250px;
    height: 190px;
    background: #222;
    border-radius: 12px;
    overflow: hidden;
    transition: .5s;
}

.course .container .card:hover .imgBx {
    top: -60px;
    scale: 0.75;
    box-shadow: 0 15px 45px rgba(0, 0, 0, 0.2);
}

.course .container .card .imgBx img {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    object-fit: cover;
}

.course .container .card .content {
    position: absolute;
    top: 210px;
    width: 100%;
    padding: 0 30px;
    height: 30px;
    overflow: hidden;
    text-align: center;
    transition: .5s;
    color: #000;
}

.course .container .card:hover .content {
    top: 125px;
    height: 250px;
}

.course .container .card .content h2 {
    font-size: 1.5em;
    font-weight: 700;
    color: #000;
}

.course .container .card .content p {
    color: #000;
}

.course .container .card .content a {
    position: relative;
    top: 12px;
    display: inline-block;
    padding: 12px 25px;
    background: #000;
    color: #fff;
    font-weight: 500;
    text-decoration: none;
    border-radius: 8px;
}

